INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '18','Proposta Pedagógica','','<p align=\"justify\"><font color=\"#000000\"><span class=\"texto1\"><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">O Centro de Educa&ccedil;&atilde;o Oficina dos Sonhos tem como proposta uma educa&ccedil;&atilde;o que contemple a forma&ccedil;&atilde;o do aluno atrav&eacute;s do desenvolvimento cognitivo, f&iacute;sico, afetivo e social. Contudo, educa&ccedil;&atilde;o n&atilde;o &eacute; apenas o processo de transferir conte&uacute;dos acad&ecirc;micos, mas todo conjunto de instru&ccedil;&otilde;es, disciplinas e pr&aacute;ticas que visam preparar esta gera&ccedil;&atilde;o para cumprir plenamente sua voca&ccedil;&atilde;o e o seu papel na sociedade, fundamentada </span></span><span class=\"texto1\"><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">em uma Educa&ccedil;&atilde;o</span></span><span class=\"texto1\"><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"> Crist&atilde;.</span></span><span style=\"font-size: 10pt; color: black; font-family: &#39;Trebuchet MS&#39;\"> Por isso, nos empenhamos tanto em construir conhecimentos, quanto em ensinar valores que s&atilde;o a base para que, no futuro, o aluno seja um adulto feliz, capacitado e consciente.</span></font></p>','<p align=\"justify\"><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">O Ensino Fundamental &eacute; uma continuidade do trabalho realizado na Educa&ccedil;&atilde;o Infantil. A a&ccedil;&atilde;o pedag&oacute;gica continua se desenvolvendo atrav&eacute;s do equil&iacute;brio entre os interesses do aluno e a interven&ccedil;&atilde;o do trabalho do professor, cujo objetivo &eacute; o desenvolvimento de uma postura cr&iacute;tica e participativa, incentivando o gosto pelas descobertas e a valoriza&ccedil;&atilde;o das experi&ecirc;ncias individuais e coletivas. </font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">Todo o trabalho tem o objetivo de desenvolver o racioc&iacute;nio criativo, construir o conhecimento atrav&eacute;s de pesquisa e fundamentar o aprendizado na aplica&ccedil;&atilde;o de Princ&iacute;pios B&iacute;blicos.</font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">&nbsp;</font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">Temos a certeza de que o exemplo &eacute; uma ferramenta fundamental no processo do ensino e aprendizagem.</font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">&nbsp;</font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">N&atilde;o basta ensinar o caminho; &eacute; preciso ensinar no caminho.</font></span><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">&nbsp;</font></span></p>\r\n<p align=\"justify\"><font color=\"#000000\"><em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">&quot;Ensina a crian&ccedil;a no caminho em </span></strong></em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">que deve </span></strong><em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">andar e ainda quando for velho </span></strong></em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">n&atilde;o </span></strong><em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">se desviar&aacute; dele&quot; Prov&eacute;rbios </span></strong></em><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">22:6</span></strong></font></p><p align=\"justify\"><font color=\"#000000\"></font><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">&nbsp;</font></span></strong><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\"><font color=\"#000000\">F&aacute;bia S. Poffo</font></span></strong></p><p align=\"justify\"><strong><span style=\"font-size: 10pt; font-family: &#39;Trebuchet MS&#39;\">Coordena&ccedil;&atilde;o Pedag&oacute;gica</span></strong></p>','1','9','0','19','2006-05-24 01:46:05','62','','2006-08-28 22:42:10','63','0','0000-00-00 00:00:00','2006-05-24 01:44:41','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','4','0','5','','','0','610','proposta-pedaga');
