INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '70','Visita Caverna de Botuverá...','','<strong><font size=\"3\"><font color=\"#000000\"><font face=\"Times New Roman\"><img src=\"http://www.oficinadossonhos.com.br/images/stories/chamada caverna.jpg\" width=\"160\" height=\"120\" hspace=\"6\" alt=\"Image\" title=\"Image\" border=\"0\" />Visita nas Grutas de Botuver&aacute; e no Observat&oacute;rio de Brusque</font></font></font></strong> <p style=\"margin: 0cm 0cm 0pt; text-align: justify\" class=\"MsoNormal\"><font face=\"Times New Roman\" size=\"3\" color=\"#000000\">A turma da 5&ordf; s&eacute;rie foi a uma viagem de estudos, &ldquo;uma aventura&rdquo;; como relatou o grupo animado.</font></p><p style=\"margin: 0cm 0cm 0pt; text-align: justify\" class=\"MsoNormal\"><font face=\"Times New Roman\" size=\"3\" color=\"#000000\">As grutas possibilitaram a visualiza&ccedil;&atilde;o de conte&uacute;dos trabalhados em sala<span>&nbsp; </span>e que s&atilde;o pouco comuns no dia-a-dia.</font></p><p style=\"margin: 0cm 0cm 0pt; text-align: justify\" class=\"MsoNormal\"><font face=\"Times New Roman\" size=\"3\" color=\"#000000\">O Observat&oacute;rio situado em Brusque promoveu o enriquecimento dos conte&uacute;dos de Ci&ecirc;ncias como o Sistema Solar.</font></p><strong><font size=\"3\"><font color=\"#000000\"><font face=\"Times New Roman\">Dicas de lugares sensacionais para vivenciar grandes experi&ecirc;ncias. <a href=\"index.php?option=com_zoom&amp;Itemid=99999999&amp;catid=34\">Confira!!!</a>&nbsp;</font></font></font></strong>','','1','1','0','1','2006-10-20 18:48:55','68','','2006-10-20 19:20:45','68','0','0000-00-00 00:00:00','2006-10-20 18:45:07','0000-00-00 00:00:0