INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '130','Judô','','<p align=\"center\"><span style=\"font-size: 8.5pt; color: #475581; font-family: Tahoma\"><span style=\"font-size: 10pt; color: #475581; font-family: Tahoma\"><img src=\"http://www.oficinadossonhos.com.br/images/stories/judo.jpg\" width=\"449\" height=\"69\" hspace=\"6\" alt=\"Image\" title=\"Image\" border=\"0\" /></span></span></p><p align=\"justify\"><span style=\"font-size: 8.5pt; color: #475581; font-family: Tahoma\"><span style=\"font-size: 10pt; color: #475581; font-family: Tahoma\">Um&nbsp;estilo de luta que hoje em dia denominamos como Jud&ocirc; foi idealizado no ano de 1882. Um jovem de 23 anos chamado Jigoro Kano fundava o Instituto Kodokan, que veio a se tornar a Meca dos ensinamentos sobre esta arte marcial. <br /><br />Com milhares de praticantes e federa&ccedil;&otilde;es espalhados pelo mundo, o Jud&ocirc; se tornou um dos esportes mais praticados, representando um nicho de mercado fiel e bem definido. N&atilde;o restringindo seus adeptos a homens com vigor f&iacute;sico, e estendendo seus ensinamentos para mulheres, crian&ccedil;as e idosos, o jud&ocirc; teve um aumento significativo no n&uacute;mero de amantes desta nobre arte. <br /><br />O Jud&ocirc; tem como filosofia integrar corpo e mente. Sua t&eacute;cnica utiliza os m&uacute;sculos e a velocidade de racioc&iacute;nio para dominar o oponente A vit&oacute;ria, ainda segundo seu mestre fundador, representa um fortalecimento espiritual. Nas academias, procura-se passar algo mais al&eacute;m da luta, do contato f&iacute;sico. Para tornar-se um bom lutador, antes de tudo, &eacute; preciso ser um grande ser humano. </span><span style=\"font-size: 10pt; color: #475581; font-family: Tahoma\">&Eacute; reconhecido como um esporte saud&aacute;vel que n&atilde;o est&aacute; relacionado &agrave; viol&ecirc;ncia. <br />Um esporte de princ&iacute;pios, tamb&eacute;m considerado uma arte, uma filosofia de vida, admirado, respeitado, vitorioso e de grande prest&iacute;gio. </span><br /></span></p>','','1','11','0','22','2007-11-14 22:47:56','68','','2008-04-10 09:14:44','68','0','0000-00-00 00:00:00','2007-11-14 22:33:28','0000-00-00 00:00:00','judo.jpg','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','5','0','2','','','0','590','jud');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '36','teste','teste','<html>\r\n<head>\r\n\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n<style type=\"text/css\">\r\n<!--\r\n\r\n.style3 {font-family: \"Trebuchet MS\"}\r\n.style5 {color: #FF0000}\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<p class=\"style3 style5\"><strong>Mam&atilde;es!!!! </strong></p>\r\n<p class=\"style3\">Gostar&iacute;amos de agradecer a presen&ccedil;a de todas na Noite do Soninho!!!</p>\r\n<p class=\"style3\">  <em>Reflex&atilde;o!!! </em></p>\r\n<p class=\"style3\">“Tu criaste todas as partes internas de meu corpo, enquanto eu ainda estava no ventre da minha m&atilde;e...Eu te agrade&ccedil;o por me teres criado de maneira t&atilde;o diferente e maravilhosa.” Sl.139-13,14 </p>\r\n<p class=\"style3\">Quem poderia fazer algo t&atilde;o perfeito... <br />\r\nUma sementinha no ventre... <br />\r\nO nascimento de um filho...nove meses para desenvolver e formar a perfei&ccedil;&atilde;o... <br />\r\nBeb&ecirc;s que crescem como uma por&ccedil;&atilde;o de fermento... <br />\r\nAdolescentes cr&iacute;ticos... <br />\r\nE agora mulheres m&atilde;es... <br />\r\nQuem poderia ser o mestre desta maravilhosa obra??? <br />\r\nEm seus olhos... o olhar do amor... <br />Em seu sorriso... o cuidado... <br />\r\nEm seu cora&ccedil;&atilde;o... a d&aacute;diva do compartilhar... <br />\r\nM&atilde;e, obra divina...<br />\r\nSomente Deus poderia ser autor desta aben&ccedil;oada obra!!! <br />\r\nCom imenso carinho que recebemos voc&ecirc;s para comemorar este dia t&atilde;o especial... que preparamos cada detalhe para acolher todas voc&ecirc;s neste Jardim t&atilde;o florido: violetas, margaridas, tulipas, rosas... </p>\r\n<p class=\"style3\"><em>Parab&eacute;ns pelo sempre Dia das M&atilde;es!!! </em></p>\r\n<p class=\"style3\">Com carinho,</p>\r\n<span class=\"style3\">Fam&iacute;lia Oficina dos Sonhos </span>\r\n<p align=\"center\" class=\"style3\">&nbsp; </p>\r\n<p align=\"center\"><span class=\"style2\"></span>\r\n</p>\r\n<p align=\"center\" class=\"style1\">&nbsp;</p>\r\n</body>\r\n</html>\r\n','','1','2','0','3','2006-06-12 18:51:36','62','','0000-00-00 00:00:00','0','0','0000-00-00 00:00:00','2006-06-12 18:51:22','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','1','','','0','0','teste');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '37','Museu Sambaqui e Parque Caieiras','','<html>\r\n<head>\r\n\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n<style type=\"text/css\">\r\n<!--\r\n\r\n.style3 {font-family: \"Trebuchet MS\"}\r\n.style5 {color: #FF0000}\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<p align=\"justify\" class=\"style3\">A 5&ordf; s&eacute;rie realizou no dia 26 de maio um passeio de estudo pelo Parque Ambiental da Caieira e Museu do Sambaqui. O Parque da Caieira que abriga preciosos ecossistemas de manguezais e restingas, tamb&eacute;m mant&eacute;m &aacute;reas de sambaquis preservadas e o Museu do Sambaqui re&uacute;ne mais de 10.000 pe&ccedil;as arqueol&oacute;gicas. </p>\r\n<p align=\"justify\"><span class=\"style3\">Foi uma manh&atilde; de muito aprendizado, que enriqueceu ainda mais as aulas de hist&oacute;ria ministradas pela professora Gizele de C. Barros</span>. </p>\r\n<p class=\"style3 style5\">&nbsp;\r\n</p>\r\n<p align=\"center\" class=\"style3\">&nbsp; </p>\r\n<p align=\"center\"><span class=\"style2\"></span>\r\n</p>\r\n<p align=\"center\" class=\"style1\">&nbsp;</p>\r\n</body>\r\n</html>\r\n','','0','1','0','1','2006-06-12 18:59:33','62','','0000-00-00 00:00:00','0','0','0000-00-00 00:00:00','2006-06-12 18:58:45','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','59','','','0','780','museu-sambaqui-e-parque-caieiras');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '38','Passeio para Estrada Bonita','','<html>\r\n<head>\r\n\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n<style type=\"text/css\">\r\n<!--\r\n\r\n.style3 {font-family: \"Trebuchet MS\"}\r\n.style5 {color: #FF0000}\r\n.style6 {\r\n	color: #990000;\r\n	font-weight: bold;\r\n	font-family: \"Trebuchet MS\";\r\n}\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<p class=\"style6\">Passeando pelo Meio Rural... </p>\r\n<p class=\"style3\">Foi na Estrada Bonita que as turmas de 3&ordf; s&eacute;rie, se deliciaram com a vida do homem do campo. </p>\r\n<p class=\"style3\">Com alegria aproveitaram o S&iacute;tio do Sr. Ango, para observar tudo aquilo que a natureza tem a oferecer, al&eacute;m da linda paisagem, o cultivo da cana-de-a&ccedil;ucar, as partes do rio, os instrumentos de trabalho, o filtro alem&atilde;o, a cria&ccedil;&atilde;o de animais entre outros. </p>\r\n<p class=\"style3\">Tiveram tamb&eacute;m a oportunidade de vivenciar o conte&uacute;do: sistema monet&aacute;rio, discutido em sala de aula, com as compras dos produtos artesanais. </p>\r\n<p class=\"style3\">E para finalizar a aula de campo, uma paradinha para um delicioso pastel caseiro. </p>\r\n<p align=\"justify\" class=\"style3\">&nbsp;</p>\r\n<p class=\"style3 style5\">&nbsp;\r\n</p>\r\n<p align=\"center\" class=\"style3\">&nbsp; </p>\r\n<p align=\"center\"><span class=\"style2\"></span>\r\n</p>\r\n<p align=\"center\" class=\"style1\">&nbsp;</p>\r\n</body>\r\n</html>\r\n','','0','1','0','1','2006-06-12 19:07:09','62','','0000-00-00 00:00:00','0','0','0000-00-00 00:00:00','2006-06-12 19:06:19','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','58','','','0','559','passeio-para-estrada-bonita');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '39','Passeio no Museu da Fundição','','<html>\r\n<head>\r\n\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\">\r\n<style type=\"text/css\">\r\n<!--\r\n\r\n.style3 {font-family: \"Trebuchet MS\"}\r\n.style5 {color: #FF0000}\r\n.style6 {\r\n	color: #990000;\r\n	font-weight: bold;\r\n	font-family: \"Trebuchet MS\";\r\n}\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<p class=\"style3\"> A turma da pr&eacute;-escola, dia 15 de maio, realizou um passeio ao Museu de Fundi&ccedil;&atilde;o Tupy com o intuito de permitir que as crian&ccedil;as observassem a possibilidade de reutilizar materiais utilizados no dia-a-dia, colaborando com o ambiente atrav&eacute;s da redu&ccedil;&atilde;o do lixo. </p>\r\n<p align=\"justify\" class=\"style3\">&nbsp;</p>\r\n<p class=\"style3 style5\">&nbsp;\r\n</p>\r\n<p align=\"center\" class=\"style3\">&nbsp; </p>\r\n<p align=\"center\"><span class=\"style2\"></span>\r\n</p>\r\n<p align=\"center\" class=\"style1\">&nbsp;</p>\r\n</body>\r\n</html>\r\n','','0','1','0','1','2006-06-12 19:09:15','62','','0000-00-00 00:00:00','0','0','0000-00-00 00:00:00','2006-06-12 19:08:27','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','57','','','0','484','passeio-no-museu-da-fundi');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '131','Técnica Vocal','','<span class=\"style272\"><h5 style=\"margin: auto 0cm\" class=\"style266\" align=\"justify\"><span style=\"font-weight: normal; font-size: 10pt\">Nas aulas de t&eacute;cnica vocal usamos a voz a servi&ccedil;o da m&uacute;sica, utiliza o sopro e possibilita modular, enriquecer e sustentar as sonoridades vocais e torn&aacute;-las mais expressivas.</span><span style=\"font-size: 10pt\"><br /></span><span style=\"font-weight: normal; font-size: 10pt\">Entre o corpo e a voz existe uma &iacute;ntima rela&ccedil;&atilde;o. &Eacute; com eles que o cantor exterioriza sua afetividade e desempenha o papel intermedi&aacute;rio entre o p&uacute;blico e a obra musical. Mas, para isso &eacute; preciso que ele possua uma t&eacute;cnica precisa e impec&aacute;vel a fim de poder dominar as in&uacute;meras dificuldades que vai encontrar.</span><span class=\"style2721\"><span style=\"font-weight: normal; font-size: 10pt; font-family: &#39;Times New Roman&#39;\"><span>&nbsp;</span><span>&nbsp;</span>Nas aulas de canto &eacute; <span>&nbsp;</span>abordado todos os aspectos da arte de cantar. </span></span><span class=\"style2721\"><span style=\"font-weight: normal; font-size: 10pt; font-family: &#39;Times New Roman&#39;\">Voc&ecirc; aprende todas as t&eacute;cnicas e a import&acirc;ncia pr&aacute;tica dos recursos de t&eacute;cnica vocal. </span></span></h5></span>','','1','11','0','22','2007-11-14 23:53:06','68','','2008-04-10 09:15:04','68','0','0000-00-00 00:00:00','2007-11-14 23:47:10','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','3','0','1','','','0','294','tica-vocal');
