INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '144','PROJETO: QUEM FAZ O QUÊ?','','<p align=\"center\"><span style=\"font-family: &#39;Comic Sans MS&#39;\" class=\"mosimage_caption\"><font size=\"2\">Per&iacute;odo: 14/04 a 02/05</font></span><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></p><p align=\"justify\"><font size=\"2\"><em style=\"font-family: &#39;Comic Sans MS&#39;\">&ldquo;</em></font><font size=\"2\"><em style=\"font-family: &#39;Comic Sans MS&#39;\"><span>E tamb&eacute;m que todo o homem coma e beba, </span></em></font><font size=\"2\"><em style=\"font-family: &#39;Comic Sans MS&#39;\"><span>e goze do bem de todo o seu trabalho; isto &eacute; um dom de Deus&rdquo;</span></em></font><em><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">(Eclesiastes 3:13)</font></span></em><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></p><p align=\"justify\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">O projeto visa ampliar o repert&oacute;rio das crian&ccedil;as sobre as profiss&otilde;es. Ser&aacute; o momento das crian&ccedil;as falar sobre as profiss&otilde;es que conhecem, principalmente a dos pais, e outras ocupa&ccedil;&otilde;es que gostam. Alguns t&oacute;picos a ser explorados:</font></span><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></p>','<p align=\"justify\"><span style=\"font: 7pt &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><font size=\"2\"><em><span style=\"font-family: &#39;Comic Sans MS&#39;\"><span>&nbsp;</span>&ldquo;Por acaso Ele n&atilde;o &eacute; o filho do carpinteiro? Mateus 13:55. </span></em><span style=\"font-family: &#39;Comic Sans MS&#39;\">O pai de Jesus era carpinteiro. E o seu?<em>&rdquo;. </em>Pesquisa com o pai e m&atilde;e sobre suas profiss&otilde;es.</span></font>&nbsp;<span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></p><p align=\"justify\"><font size=\"2\"><span style=\"font-family: Symbol\"><span>&middot;<span style=\"font: 7pt &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family: &#39;Comic Sans MS&#39;\">Mostrar &agrave; crian&ccedil;a como as profiss&otilde;es se articulam e como TODAS s&atilde;o importantes para o bom funcionamento da sociedade.&nbsp;</span></font></p><p align=\"justify\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span><font size=\"2\"><span style=\"font-family: Symbol\"><span>&middot;<span style=\"font: 7pt &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family: &#39;Comic Sans MS&#39;\">O que quero ser quando crescer?&nbsp;</span></font></p><p align=\"justify\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span><font size=\"2\"><span style=\"font-family: Symbol\"><span>&middot;<span style=\"font: 7pt &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family: &#39;Comic Sans MS&#39;\">Tem gente que n&atilde;o gosta de nenhum tipo de trabalho... Prov&eacute;rbios 6:6-8 diz:</span><span style=\"font-family: &#39;Comic Sans MS&#39;\"> <em>&ldquo;Pregui&ccedil;oso, aprenda uma li&ccedil;&atilde;o com as formigas! Elas n&atilde;o tem l&iacute;der, nem chefe, nem governador, mas guardam comida no ver&atilde;o, preparando-se para o inverno.&rdquo; </em><span>&nbsp;</span></span></font><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></p>','1','8','0','30','2008-04-23 11:02:22','68','','2008-04-23 11:09:03','68','0','0000-00-00 00:00:00','2008-04-23 11:00:56','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','4','0','3','','','0','266','projeto-quem-faz-o-qu');
