INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '140','PROJETO: EU, VOCÊ, NÓS...','','<div class=\"article_seperator\" align=\"justify\" style=\"width: 460px; height: 242px\"><h2 style=\"font-family: &#39;Comic Sans MS&#39;\" align=\"center\"><span class=\"mosimage_caption\"><span style=\"font-family: &#39;Comic Sans MS&#39;\">Per&iacute;odo: 18/02 a 14/03</span><span style=\"font-family: &#39;Comic Sans MS&#39;\">&nbsp;</span> </span></h2><h2 style=\"font-family: &#39;Comic Sans MS&#39;\" align=\"center\"><span style=\"font-family: &#39;Comic Sans MS&#39;\" class=\"mosimage_caption\">&ldquo;Porque foi Deus quem nos fez...&rdquo;</span></h2><h2 style=\"font-family: &#39;Comic Sans MS&#39;\" align=\"center\"><span class=\"mosimage_caption\">&nbsp;<span style=\"font-family: &#39;Comic Sans MS&#39;\">(Ef&eacute;sios 2:10a)</span> </span></h2><h6 style=\"font-family: &#39;Comic Sans MS&#39;\">&ldquo;Eu, voc&ecirc;, n&oacute;s...&quot; partiu do tema da forma&ccedil;&atilde;o da identidade, buscando levar a crian&ccedil;a a perceber-se como indiv&iacute;duo, a situar-se, a aprender a diferenciar seus gostos e opini&otilde;es e, progressivamente, a compreender e respeitar a exist&ecirc;ncia do outro, sempre dentro dos Princ&iacute;pios da Educa&ccedil;&atilde;o Crist&atilde;. <span style=\"font-family: &#39;Comic Sans MS&#39;\">A identidade se molda nos limites das outras refer&ecirc;ncias que nos constituem. Ao mesmo tempo, somos n&oacute;s, mas tamb&eacute;m nossos pais, nossos amigos, nossas experi&ecirc;ncias, ou seja, nos constitu&iacute;mos a partir de nossas rela&ccedil;&otilde;es.</span><span style=\"font-family: &#39;Comic Sans MS&#39;\"><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\">A participa&ccedil;&atilde;o de toda fam&iacute;lia nas atividades propostas foi muito importante, pois cada uma p&ocirc;de se envolver ainda mais com as descobertas de seus filhos. </font></span><span style=\"font-size: 9pt\"><font face=\"Times New Roman\" color=\"#000000\">&nbsp;</font></span> <a href=\"index.php?option=com_zoom&amp;Itemid=99999999&amp;catid=93\">Confira as fotos!!</a></span><span style=\"font-family: &#39;Comic Sans MS&#39;\"> <p style=\"margin: 0cm 0cm 0pt; text-indent: 35.45pt; text-align: justify\" class=\"MsoNormal\" align=\"justify\">&nbsp;</p></span></h6></div><p style=\"font-family: &#39;Comic Sans MS&#39;\" align=\"justify\">&nbsp;</p>','','1','8','0','30','2008-04-09 16:58:12','68','','2008-04-10 11:00:32','68','0','0000-00-00 00:00:00','2008-04-09 16:55:15','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','16','0','5','','','0','173','projeto-eu-voc-n');
