INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '143','PROJETO: A FAMÍLIA QUE DEUS ME DEU','','<div align=\"center\"><strong class=\"mosimage_caption\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">Per&iacute;odo: 24/03 a 11/04</font></span><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></strong></div><div align=\"center\"></div><div align=\"center\"><strong class=\"mosimage_caption\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&ldquo;Tenham muitos e muitos filhos; espalhem-se por toda a&nbsp;terra e a </font></span></strong></div><div align=\"center\"></div><div align=\"center\"><strong class=\"mosimage_caption\"><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">dominem.&rdquo;</font></span><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">(G&ecirc;nesis 1:28)</font></span><em><span style=\"font-family: &#39;Comic Sans MS&#39;\"><font size=\"2\">&nbsp;</font></span></em> </strong></div><p style=\"margin: 0cm 0cm 0pt; text-indent: 35.45pt; text-align: justify\" align=\"center\"><font color=\"#000000\"><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\">Ap&oacute;s criar Ad&atilde;o e Eva, Deus os uniu como marido e mulher, aben&ccedil;oou-os e ent&atilde;o lhes disse: &ldquo;Tenham muitos e muitos filhos; espalhem-se por toda a terra e a dominem.&rdquo; Era prop&oacute;sito de Deus que a Terra fosse povoada com seres criados &agrave; Sua pr&oacute;pria imagem, compondo fam&iacute;lias que trariam gl&oacute;ria a Ele (Isa&iacute;as 45:18).</span><span style=\"font-size: 9pt\"><font face=\"Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font></span></font></p><p align=\"justify\"><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\">A fam&iacute;lia &eacute; o princ&iacute;pio da sociedade. E a fam&iacute;lia crist&atilde; &eacute; aquela em que Deus &eacute; reconhecido como objeto supremo de adora&ccedil;&atilde;o. Ele &eacute; a cabe&ccedil;a, protetor, guia e instrutor de fam&iacute;lias assim. </font></span><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\">A fam&iacute;lia tamb&eacute;m &eacute; escola e seus membros s&atilde;o professores e alunos que compartilham conhecimento e aprendem uns com os outros.</font></span><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\">A Palavra de Deus deve ser a principal fonte de instru&ccedil;&atilde;o na escola da fam&iacute;lia (Deuteron&ocirc;mio 6:4-9; Salmo 128:1-6).</font></span><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\">Este ser&aacute; o momento em que a crian&ccedil;a relatar&aacute; a posi&ccedil;&atilde;o de cada familiar, os acontecimentos marcantes em sua presen&ccedil;a e tamb&eacute;m momentos de lazer, h&aacute;bitos alimentares, prefer&ecirc;ncias, costumes...</font></span></p><p align=\"center\"><span style=\"font-size: 9pt; font-family: &#39;Comic Sans MS&#39;\"><font color=\"#000000\"><a href=\"index.php?option=com_zoom&amp;Itemid=99999999&amp;catid=99\">Confira as fotos!!</a></font></span></p>','','1','8','0','30','2008-04-10 10:23:55','68','','2008-04-23 10:56:53','68','0','0000-00-00 00:00:00','2008-04-10 10:22:09','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','10','0','4','','','0','160','projeto-a-famia-que-deus-me-deu');
